
#include "../execution.h"

/* Libère la mémoire allouée pour la liste chaînée des variables d'environnement */
void	free_env(t_env *env)
{
	t_env	*current;
	t_env	*next;

	current = env;
	while (current)
	{
		next = current->next;
		free(current->key);
		free(current->value);
		free(current);
		current = next;
	}
}
